class SNTDeployer:

    def __init__(self, smart_contract_runner: SmartContractRunner):
        self.deploy_output = smart_contract_runner.clone_and_run(
            github_org="status-im",
            github_repo="status-network-token-v2",
            smart_contract_dir="script",
            smart_contract_filename="Deploy.s.sol",
            private_key=DEPLOYER_ACCOUNT.private_key,
            sender_address=DEPLOYER_ACCOUNT.address,
        )

        self.snt_contract_address = self._get_snt_contract_address(self.deploy_output)
        self.snt_token_controller_address = self._get_snt_token_controller_address(self.deploy_output)

    def _get_snt_contract_address(self, deploy_output):
        for deployment in deploy_output.values():
            if deployment["internal_type"] == "contract SNTV2":
                return deployment["value"]
        raise Exception("SNTV2 contract not found")

    def _get_snt_token_controller_address(self, deploy_output):
        for deployment in deploy_output.values():
            if deployment["internal_type"] == "contract SNTTokenController":
                return deployment["value"]
        raise Exception("SNTTokenController contract not found")
